import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      v:false,
      data: []
    }
  }

  handleCheck(event) {
    const value = event.target.value;
    console.log(value)
  }

  componentDidMount() {
    list=[{ name: 'raju', age: 19 }, { name: 'ramu', age: 26 }, { name: 'ravan', age: 25 }, { name: 'laxman', age: 30 }]
    value=this.state.v
    for(var i=0;i<list;i++)
    {
      if(list[i].age>18 && list[i].age<25){
        value=true
        console.log(value)
        break
      }
    }
    this.setState({ data: list,v:value
   })
  }
  render() {
    console.log(this.state.data.length)
   var value='no';
    for(var i=0;i<this.state.data.length;i++)
    {console.log('hello')
        if(this.state.data[i].age>18 && this.state.data[i].age<25){
          value='yes'
          console.log(value)
          break
        }

    }
    console.log(value)
    return (
      <div>
        <ul>
          {this.state.data.map(item => {
            return <li>{item.name}-{item.age}</li>;
          })}
        </ul>
        Age Between 18 to 25:
        <p>{value}</p>
        Yes:<input type="checkbox" defaultChecked={value==='yes'?true:false} />

        No:<input type="checkbox" defaultChecked={value==='no'?false:true} />

        {/* i used console.log for what data function returned its not related to condition*/}
      </div>
    );
  }
}

export default App;
