import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      checked: false
    }
  }

  handleCheck(event) {
    const value = event.target.value;
    console.log(this.state.data)
  }

  componentDidMount() {
    this.setState({ data: [{ name: 'raju', age: 23 }, { name: 'ramu', age: 26 }, { name: 'ravan', age: 25 }, { name: 'laxman', age: 30 }] })
    if(this.state.data.filter((data)=>{ data.age >= 18})){
      this.setState({checked:true})
    }
  }
  render() {
    return (
      <div>
        <ul>
          {this.state.data.map(item => {
            return <li>{item.name}-{item.age}</li>;
          })}
        </ul>
        Yes:<input type="checkbox" onChange={this.handleCheck.bind(this)} />
        No:<input type="checkbox" onChange={this.handleCheck.bind(this)}  />
      </div>
    );
  }
}

export default App;
