import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      checked:false
    }
  }

  handleCheck(event) {
    const value = event.target.value;
    console.log(value)
  }

  componentDidMount() {
    this.setState({ data: [{ name: 'raju', age: 18}, { name: 'ramu', age: 17 }, { name: 'ravan', age: 13 }, { name: 'laxman', age: 12 }] })
  }
  handleChange =()=>{
    alert("hello");
    this.state.data.filter((data)=>{
      return (data.age >= 18) === true ?(this.setState({checked:!this.state.checked})):(this.state.checked) 
    })

  }

  render() {


    return (
      <div>
        <ul>
          {this.state.data.map(item => {
            return <li>{item.name}-{item.age}</li>;
          })}
        </ul>
        Yes:<input type="checkbox"  onChange={this.handleChange.bind(this)} defaultChecked={this.state.checked}  />      
      </div>
    );
  }
}

export default App;
