  constructor(props) {
    super(props);
    this.state = {

      data: []
    }
  }

  handleCheck(event) {
    const value = event.target.value;
    console.log(value)
  }

  componentDidMount() {
    this.setState({ data: [{ name: 'raju', age: 19 }, { name: 'ramu', age: 26 }, { name: 'ravan', age: 25 }, { name: 'laxman', age: 30 }] })
  }
  render() {
    console.log(this.state.data.length)
