    {console.log('hello')
        if(this.state.data[i].age>18 && this.state.data[i].age<25){
          v=1
          console.log(v)
          break
        }

    }
    console.log(v)
    return (
      <div>
        <ul>
          {this.state.data.map(item => {
            return <li>{item.name}-{item.age}</li>;
          })}
        </ul>
        Age Between 18 to 25:
        <p>{String(v)}</p>
        Yes:<input type="checkbox" defaultChecked={v===1} />

        No:<input type="checkbox" defaultChecked={v===0} />

        {/* i used console.log for what data function returned its not related to condition*/}
      </div>
    );
  }
}

export default App;
